def feature(text: str) -> float:
    "Standard deviation of word lengths in the text"
    words = text.split()
    if not words:
        return 0.0
    mean_length = sum(len(word) for word in words) / len(words)
    variance = sum((len(word) - mean_length) ** 2 for word in words) / len(words)
    return variance ** 0.5

